INSERT INTO `jos_gmapfp` VALUES (20, 'Garden', 'restgarden', 'San Juan Bosco, 3', 'ZARAGOZA', 'ARAGÓN', '50006', 'SPAIN', '+34976551625', 'restaurante@restaurantegarden.com', 'www.restaurantegarden.com', 'logoggarden.png', 0, '<p>Restaurantes de Bodas en Zaragoza, celebra tu boda en Zaragoza con nosotros, mira los salones de nuestro restaurante y comprueba nuestros precios. Garden, tu Boda en Zaragoza.</p>', '', '', 0, '', '', 0, '', '-0.900246899', '41.644692', '5', 5, 67, 1, '', 0, 0, 'Restaurantes de Bodas en Zaragoza, celebra tu boda en Zaragoza con nosotros, mira los salones de nuestro restaurante y comprueba nuestros precios. Garden, tu Boda en Zaragoza.', 'Restaurante,Zaragoza,Menu diario,bar,pub,gestion restaurante,oferta', 'robots=index,follow\r\nauthor=info@amimenu', 333, 101, '2011-06-23 11:13:58', 67, '2011-06-23 11:13:58', '2011-04-13 14:31:14', '0000-00-00 00:00:00', 143, 'show_author=', 'No', '10€', 'entre 5€ y 10€', 'No', 'Obsequio de la casa', 'Si', 50, '<p><span style="font-size: medium;" size="4"><span style="font-size: 15px;" class="Apple-style-span"><b>Descanso semanal</b></span></span></p>', '<div style="line-height: 12px;">\r\n<p style="text-align: left;" align="center"><em style="font-size: 1.2em;">Alcachofas estofadas con jamón  <br />Espaguetis al ajillo con palitos y gambas  <br />Garbanzos con pisto  <br />Ensaladilla rusa con huevo  <br />Ensalada de champiñones con pasas y beicon </em></p>\r\n<p style="text-align: left;" align="center"><em style="font-size: 1.2em;">Besugo rojo al orio  <br />Bacalao a la riojana <br />Escalopines de ternera a la cerveza  <br />Albondigas de carne con tomate  <br />Huevos al plato con setas y jamón </em></p>\r\n<p style="text-align: left;" align="center"><i><br /></i></p>\r\n<p style="text-align: left;"><em style="font-size: 1em;">Postres Variados</em></p>\r\n</div>', '<div style="line-height: 10px;">\r\n<p></p>\r\n<div style="line-height: 10px;">\r\n<p style="text-align: left;"><span style="line-height: 10px; color: #3366ff;"><strong>Si reservas para grupo de 10 o más, cortesía de la casa</strong></span></p>\r\n<p style="text-align: left;" align="center"></p>\r\n<p style="text-align: left;" align="center"><em style="font-size: 1.2em;">Pastel de patata con carne gratinado <br />Salteado de garbanzos con verduras y gambas <br />Tortellinis a la napolitana <br />Marmitako de bonito <br />Ensalada de tomate con queso fresco y anchoas</em> </p>\r\n<p style="text-align: left;" align="center"></p>\r\n<p style="text-align: left;" align="center"><em style="font-size: 1.2em;">Lubina al orio <br />Merluza en salsa verde <br />Escalope de ternera con patatas <br />Supremas de ave al Oporto con champiñones <br />Revuelto de trigueros y gambas </em></p>\r\n<p style="text-align: left;" align="center"></p>\r\n<p style="text-align: left;"><em style="font-size: 1.2em;">Postres Variados</em></p>\r\n<p style="text-align: left;" align="center"><span style="font-size: small;" size="3"><span style="font-size: 13px;" class="Apple-style-span"><i><br /></i></span></span></p>\r\n</div>\r\n<p style="text-align: left;"><span style="color: #3366ff;" color="#3366FF"><b><br /></b></span></p>\r\n</div>', '<div style="line-height: 10px;">\r\n<p style="text-align: left;"><span style="line-height: 10px; color: #3366ff;"><strong>Si reservas para grupo de 10 o más, cortesía de la casa</strong></span></p>\r\n<p style="text-align: left;" align="center"></p>\r\n<p style="text-align: left;" align="center"><em style="font-size: 1.2em;">Paella mixta <br />Menestra de verduras con jamón <br />Espaguettis a la carbonara <br />Rollitos de jamón york con espárragos <br />Ensalada de cogollos con atún y salsa de anchoas</em> </p>\r\n<p style="text-align: left;" align="center"></p>\r\n<p style="text-align: left;" align="center"><em style="font-size: 1.2em;">Filete de mero en salsa con setas <br />Pimientos rellenos de chipirones <br />Salchichas fritas al vino blanco <br />Muslos de pollo asados al limón <br />Huevos al plato flamenca <br /></em></p>\r\n<p style="text-align: left;" align="center"><em style="font-size: 1.2em;">Vinos - Minerales y Pan</em></p>\r\n</div>', '<div style="line-height: 10px;">\r\n<p style="color: #235b79; text-align: left;"><strong>Si reservas para grupo de 10 o más, cortesía de la casa</strong></p>\r\n<p style="text-align: left;" align="center"><em style="font-size: 1.2em;">Brocolí gratinado con baicon<br />Alubias rojas con chorizo<br />Macarrones a la boloñesa<br />Ensalada de pollo con piña y salsa rosa<br />Ensalada de tomate con queso tierno y anchoas</em></p>\r\n<p style="text-align: left;" align="center"><em style="font-size: 1.2em;">Besugo rojo al orio<br />Filete de mero en salsa verde<br />Albondigas en salsa de almendras<br />Medallones de ternera a la cerveza<br />Revuelto de champiñones con jamón</em></p>\r\n<p><em style="font-size: 1.2em;">Postres Variados<br /></em></p>\r\nPostre,Pan, Vino o Agua</div>', '<p><span style="color: #235b79; line-height: 10px;"><strong>Si reservas para grupo de 10 o más, cortesía de la casa</strong></span></p>\r\n<div style="line-height: 12px;">\r\n<p style="text-align: left;" align="center"></p>\r\n<p style="text-align: left;" align="center"><em style="font-size: 1.2em;">Arroz negro con calamares y gambas <br />Espárragos calientes con salmón ahumado y muselina <br />Alubias rojas con costilla <br />Lasaña de cinco setas gratinada <br />Entremés variado con fritos</em></p>\r\n<p style="text-align: left;" align="center"></p>\r\n<p style="text-align: left;" align="center"><em style="font-size: 1.2em;">Besugo rojo al orio con gulas <br />Bacalao a la riojana <br />Carrillera asada al horno con patatas <br />Bistec de ternera a la plancha con guarnición <br />Revuelto de setas con gambas</em></p>\r\n<em style="font-size: 1.2em;"></em>\r\n<p style="text-align: left;"><em style="font-size: 1.2em;">Postres Variados</em></p>\r\n<br />\r\n<div></div>\r\n</div>', '<p><span style="color: #235b79; line-height: 10px;"><strong>Si reservas para grupo de 10 o más, cortesía de la casa</strong></span></p>\r\n<div style="line-height: 12px;"><span style="font-size: 8pt;">ESPECIAL COMUNIONES, LLAMANOS E INFORMATE. </span></div>', '10€', '10€', '10€', '10€', '10€', '10€', '10€', 0, NULL, 0);
